package com.dev.intourist.domain.usecase.confirmimport com.dev.intourist.domain.model.confirm.ConfirmModelimport com.dev.intourist.domain.model.confirm.ResendConfirmModelimport com.dev.intourist.domain.model.recover.RecoverModelimport com.dev.intourist.domain.repository.confirm.ConfirmRepositoryclass ConfirmUseCase(private val repository: ConfirmRepository) {    fun confirm(code: ConfirmModel) = repository.confirm(code)    fun resendRecover(email: RecoverModel) = repository.resendRecover(email)    fun resendConfirm(userName: ResendConfirmModel) = repository.resendConfirm(userName)}