package com.dev.intourist.domain.usecase.tokenimport com.dev.intourist.domain.model.token.GetTokenModelimport com.dev.intourist.domain.model.token.RefreshTokenModelimport com.dev.intourist.domain.model.token.VerifyTokenModelimport com.dev.intourist.domain.repository.token.TokenRepositoryclass TokenUseCase(private val repository: TokenRepository) {    fun getToken(body: GetTokenModel) = repository.getToken(body)    fun refreshToken(body: RefreshTokenModel) = repository.refreshToken(body)    fun verifyToken(body: VerifyTokenModel) = repository.verifyToken(body)}