package com.dev.intourist.presentation.ui.screen.payment.other_way.bottomsheetdialogimport android.content.ActivityNotFoundExceptionimport android.content.Intentimport android.net.Uriimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Toastimport com.dev.intourist.databinding.FragmentSendCheckBinding//import com.dev.intourist.databinding.FragmentSendCheckBindingimport com.google.android.material.bottomsheet.BottomSheetDialogFragmentclass SendCheckFragment : BottomSheetDialogFragment() {    private val binding: FragmentSendCheckBinding by lazy {        FragmentSendCheckBinding.inflate(layoutInflater)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)    }    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View {//        return null        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            btnTelegram.setOnClickListener {                openTelegram("medetbekov002")            }            btnWhatsApp.setOnClickListener {                openWhatsApp("996704848277")            }        }    }    private fun openWhatsApp(phoneNumber: String) {        val url = "https://wa.me/$phoneNumber"        val intent = Intent(Intent.ACTION_VIEW).apply {            data = Uri.parse(url)            setPackage("com.whatsapp")        }        try {            startActivity(intent)        } catch (e: ActivityNotFoundException) {            Toast.makeText(                requireContext(),                "WhatsApp is not installed on your device",                Toast.LENGTH_SHORT            ).show()            try {                startActivity(                    Intent(                        Intent.ACTION_VIEW,                        Uri.parse("market://details?id=com.whatsapp")                    )                )            } catch (e: ActivityNotFoundException) {                startActivity(                    Intent(                        Intent.ACTION_VIEW,                        Uri.parse("https://play.google.com/store/apps/details?id=com.whatsapp")                    )                )            }        }    }    private fun openTelegram(username: String) {        val url = "tg://resolve?domain=$username"        val intent = Intent(Intent.ACTION_VIEW).apply {            data = Uri.parse(url)            setPackage("org.telegram.messenger")        }        try {            startActivity(intent)        } catch (e: ActivityNotFoundException) {            Toast.makeText(                requireContext(),                "Telegram is not installed on your device",                Toast.LENGTH_SHORT            ).show()            try {                startActivity(                    Intent(                        Intent.ACTION_VIEW,                        Uri.parse("market://details?id=org.telegram.messenger")                    )                )            } catch (e: ActivityNotFoundException) {                startActivity(                    Intent(                        Intent.ACTION_VIEW,                        Uri.parse("https://play.google.com/store/apps/details?id=org.telegram.messenger")                    )                )            }        }    }}