package com.dev.intourist.ui.screen.payment.other_wayimport android.content.Intentimport android.os.Bundleimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Toastimport org.koin.androidx.viewmodel.ext.android.viewModelimport androidx.fragment.app.viewModelsimport androidx.navigation.fragment.findNavControllerimport by.kirich1409.viewbindingdelegate.viewBindingimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentOtherWayBindingimport com.dev.intourist.presentation.base.fragment.BaseFragmentclass OtherWayFragment :    BaseFragment<FragmentOtherWayBinding, OtherWayViewModel>(R.layout.fragment_other_way) {    override val binding: FragmentOtherWayBinding by viewBinding(FragmentOtherWayBinding::bind)    override val viewModel: OtherWayViewModel by viewModel()    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)    }    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        // Inflate the layout for this fragment        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            toolbar.setNavigationOnClickListener {                findNavController().popBackStack()            }            buttonContinue.setOnClickListener {                findNavController().navigate(R.id.sendCheckFragment)            }                imgConfirm.setOnClickListener {                    // Логика для img_confirm                    startActivity(Intent(requireActivity(), OtherWayFragment::class.java))                }                imgCard.setOnClickListener {                    // Логика для img_card                    startActivity(Intent(requireActivity(), OtherWayFragment::class.java))                }                imgPayment.setOnClickListener {                    // Логика для img_payment                    startActivity(Intent(requireActivity(), OtherWayFragment::class.java))                }        }    }}