package com.dev.intourist.ui.screen.payment.without_childrenimport android.content.Intentimport android.os.Bundleimport android.view.Viewimport org.koin.androidx.viewmodel.ext.android.viewModelimport androidx.navigation.fragment.findNavControllerimport by.kirich1409.viewbindingdelegate.viewBindingimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentWithoutChildrenBindingimport com.dev.intourist.ui.base.fragment.BaseFragmentimport com.dev.intourist.ui.screen.payment.other_way.OtherWayFragmentclass WithoutChildrenFragment :    BaseFragment<FragmentWithoutChildrenBinding, WithoutChildrenViewModel>(R.layout.fragment_without_children) {//    private val binding : FragmentWithoutChildrenBinding by lazy {//        FragmentWithoutChildrenBinding.inflate(layoutInflater)//    }    override val binding : FragmentWithoutChildrenBinding by viewBinding(FragmentWithoutChildrenBinding::bind)    override val viewModel: WithoutChildrenViewModel by viewModel()    private var adultsCount = 1    private var babyCount = 0    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        onBtnClick()    }//    override fun onCreateView(//        inflater: LayoutInflater, container: ViewGroup?,//        savedInstanceState: Bundle?//    ): View {//        // Inflate the layout for this fragment//        return binding.root//    }    private fun onBtnClick(): View? {        binding.apply {            buttonContinue.setOnClickListener {                findNavController().navigate(R.id.byCardFragment)            }            buttonBack.setOnClickListener {                findNavController().popBackStack()            }            imgConfirm.setOnClickListener {                // Логика для img_confirm                startActivity(Intent(requireActivity(), OtherWayFragment::class.java))            }            imgCard.setOnClickListener {                // Логика для img_card                startActivity(Intent(requireActivity(), OtherWayFragment::class.java))            }            imgPayment.setOnClickListener {                // Логика для img_payment                startActivity(Intent(requireActivity(), OtherWayFragment::class.java))            }            btnCountAdultsPlus.setOnClickListener {                adultsCount++                updateAdultsCount()            }            btnCountAdultsMinus.setOnClickListener {                if (adultsCount > 1) {                    adultsCount--                    updateAdultsCount()                }            }            btnCountBabyPlus.setOnClickListener {                babyCount++                updateBabyCount()            }            btnCountBabyMinus.setOnClickListener {                if (babyCount > 0) {                    babyCount--                    updateBabyCount()                }            }            updateAdultsCount()            updateBabyCount()            return view        }    }    private fun updateAdultsCount() {        binding.txtResult.text = adultsCount.toString()    }    private fun updateBabyCount() {        binding.txtResult2.text = babyCount.toString()    }}