package com.dev.intourist.ui.screen.settingsimport android.os.Bundleimport android.view.Viewimport androidx.navigation.fragment.findNavControllerimport com.dev.intourist.Rimport com.dev.intourist.presentation.base.fragment.BaseFragmentimport org.koin.androidx.viewmodel.ext.android.viewModelimport by.kirich1409.viewbindingdelegate.viewBindingimport com.dev.intourist.databinding.FragmentSettingsBindingclass SettingsFragment :    BaseFragment<FragmentSettingsBinding, SettingsViewModel>(R.layout.fragment_settings) {    override val binding: FragmentSettingsBinding by viewBinding(FragmentSettingsBinding::bind)    override val viewModel: SettingsViewModel by viewModel()    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            titlePushNotifications.setNavigationOnClickListener {                findNavController().popBackStack()            }        }    }}