package com.dev.intourist.ui.screen.terms_of_useimport android.os.Bundleimport android.text.SpannableStringimport android.text.style.StyleSpanimport android.text.style.UnderlineSpanimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.navigation.fragment.findNavControllerimport by.kirich1409.viewbindingdelegate.viewBindingimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentTermsOfUseBindingimport com.dev.intourist.presentation.base.fragment.BaseFragmentimport org.koin.androidx.viewmodel.ext.android.viewModelclass TermsOfUseFragment :    BaseFragment<FragmentTermsOfUseBinding, TermsOfUseViewModel>(R.layout.fragment_terms_of_use) {    override val binding: FragmentTermsOfUseBinding by viewBinding(FragmentTermsOfUseBinding::bind)    override val viewModel: TermsOfUseViewModel by viewModel()    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        val view = inflater.inflate(R.layout.fragment_terms_of_use, container, false)//        val textView = view.findViewById<TextView>(R.id.tv_terms_of_use_description)        val text = "?"        val spannableString = SpannableString(text)        val end = minOf(7, text.length)        spannableString.setSpan(            StyleSpan(android.graphics.Typeface.BOLD_ITALIC),            0,            end,            0        ) // Жирный курсив        spannableString.setSpan(UnderlineSpan(), 0, end, 0) // Подчеркнутый текст//        textView.text = spannableString        return view    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            titleTermsOfUseTitle.setOnClickListener {                findNavController().popBackStack()            }        }    }}